CREATE TABLE GROUPS(ID_GROUP INT PRIMARY KEY, TITLE VARCHAR(20))
CREATE TABLE USERS(ID_USER INT PRIMARY KEY, ID_GROUP INT FOREIGN KEY REFERENCES GROUPS(ID_GROUP),FIRST_NAME VARCHAR(20),FAM_NAME VARCHAR(20),EMAIL VARCHAR(20),PASS VARCHAR(20),ABOUT VARCHAR(20))
CREATE TABLE TOPICS(ID_TOPIC INT PRIMARY KEY, ID_USER INT FOREIGN KEY REFERENCES USERS(ID_USER), DATE_MODIFIED DATETIME, TITLE VARCHAR(20))
CREATE TABLE POSTS(ID_POST INT PRIMARY KEY, ID_TOPIC INT FOREIGN KEY REFERENCES TOPICS(ID_TOPIC), DATE_CREATED DATETIME, BODY VARCHAR(MAX))
CREATE TABLE PAGES(ID_PAGE INT PRIMARY KEY, ID_USER INT FOREIGN KEY REFERENCES USERS(ID_USER),DATE_MODIFIED DATETIME, TITLE VARCHAR(20),BODY VARCHAR(MAX),IS_HOME CHAR)
CREATE TABLE CALENDAR(ID_CAL INT PRIMARY KEY, DATE_START DATETIME, DATE_END DATETIME,TITLE VARCHAR(20))
CREATE TABLE SCHEDULE(ID_SCH INT PRIMARY KEY, TIME_START DATETIME, TIME_END DATETIME, WEEKDAY VARCHAR(,TITLE VARCHAR(20))
